    let req = new XMLHttpRequest();
    req.onreadystatechange = () => {
        if (req.readyState === 4) {
            res = req.response
            res = JSON.parse(res)
        }
    }
    req.open('GET', "http://localhost:3001/get")
    req.send();
    return renderBooks(res)
}
function renderBooks(books : Array<book>) {
    let ret
    books.forEach(book => {
        ret += <Book 
                    title = { book.title } 
                    author =     { book.author } 
                    isbn =       {book.isbn}
                    cover = { book.cover }>
                </Book>
    })
    return ret
}


function Category(props : props) {
    return <div className="Category">
        <h3> {props.Name } <FontAwesomeIcon icon={faCaretDown} /></h3>
        <ul>
            { fetch(props) }
        </ul>
        <button onClick={() => fetch(props.Name)} >fetch</button>
    </div>
}

export default Category;
