import './style/App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import 'font-awesome/css/font-awesome.min.css';
import { faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';
import Category from  './Category'
import { useEffect, useState } from "react";

interface category {
  name : string;
  id : string;
}

async function fetchCategories() {
  let res = await fetch("http://localhost:3001/catgeories")
  let response = await res.json();

  console.log(response)
  return response[0].result as Array<category>;
}

function renderCategories(catgeories : Array<category>) {
  if (catgeories == undefined) return <p>Loading</p>


  return catgeories.map(category => {
    <li>
      <Category name= {category.name} id={category.id.slice(6)}/>
    </li>
  })
}

function App(props) {

   const [categories, initCategories] = useState([])

   useEffect(() => {
       fetchCategories().then((data => {initCategories(data)}))
   },[])

  return <div className="App">
      <header className="App-header">
        <section>
          <h1>BÃ¼cherei</h1>
          <h2>Regale:</h2>
        </section>
        <ul>
          <li>
            <section>
                <h2>Suche<input id="search" placeholder="|"/><FontAwesomeIcon icon={faMagnifyingGlass}/></h2>
                <Category search=""/>
            </section>
          </li>
          { renderCategories(categories) }
        </ul>
      </header>
    </div>
 }

 export default App;
