import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import 'font-awesome/css/font-awesome.min.css';
import { faCaretDown } from '@fortawesome/free-solid-svg-icons';
import Book from './Book'
import { useEffect, useState } from "react";

let books: Array<book> 

interface props {
    Name: string;
    id : string;
}
interface book {
    author: string,
    types:  Array<string>,
    title:  string,
    isbn:   string,
    cover:  string,
}
async function fetchBooks(id) {
    let res = await fetch("http://localhost:3001/get?type=types:" + id)
    let Response = await res.json();
    books = Response as Array<book>;
    let ret = books.map((b) => <li>
            <Book 
                title={b.title}
                author={b.author}
                isbn={b.isbn}
                cover={b.cover} 
                types={[]} 
            /></li>
        )

    return ret    
}


export default function Category(props) {
    const [books, initBooks] = useState([])
    useEffect(() => {
        fetchBooks(props.id).then((data => {initBooks(data)}))
    },[])
    return <div className="Category">
        <h3> {props.Name } <FontAwesomeIcon icon={faCaretDown} /></h3>
        <ul>
            { books }
        </ul>
    </div>
}
