import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import 'font-awesome/css/font-awesome.min.css';
import { faCaretDown } from '@fortawesome/free-solid-svg-icons';
import Book from './Book'
import { useEffect, useState } from "react";
import "./style/Category.css"

let books: Array<book> 

interface props {
    Name: string;
    id : string;
    hidden : boolean;
}
interface book {
    author: string,
    types:  Array<string>,
    title:  string,
    isbn:   string,
    cover:  string,
}
async function fetchBooks(id) {
    let res = await fetch("http://localhost:3001/get?type=types:" + id)
    let Response = await res.json();
    console.log(Response);
    books = Response[0].result as Array<book>;
    console.log(books);
    let ret = books.map((b) => <Book 
                title =  { b.title  }
                author = { b.author }
                isbn =   { b.isbn   }
                cover =  { b.cover  } 
                types =  {    []    }  
            />
        )

    return ret    
}

function toggleHidden() {
    hide(false)
}

export default function Category(props) {
    const [books, initBooks] = useState([])

    useEffect(() => {
        fetchBooks(props.id).then((data => {initBooks(data)}))
    },[])

    const [hidden, sethidden] = useState(false)

    return <div className="Category">
        <h3 onClick={ () => {toggleHidden(props)} } > {props.Name } <FontAwesomeIcon icon={faCaretDown} /></h3>
        <div className="shelf" style={{ display: props.hidden ? "none" : "block"}}>
            { books }
        </div>
    </div>
}
