import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import 'font-awesome/css/font-awesome.min.css';
import { faCaretDown } from '@fortawesome/free-solid-svg-icons';
import Book from './Book'
import { waitFor } from "@testing-library/react";

interface props {
    Name: string;
}
interface book {
    author: string,
    types:  Array<string>,
    title:  string,
    isbn:   string,
    cover:  string,
}
function fetch(category) {
    let req = new XMLHttpRequest();
    req.open('GET', "http://localhost:3001/get")
    req.send();

    while (req.readyState != 4) console.log(req.readyState)

    let books : Array<book> = JSON.parse(req.response) 
    let ret
    books.forEach(book => {
        ret += <Book 
                    title = { book.title } 
                    author =     { book.author } 
                    isbn =       {book.isbn}
                    cover = { book.cover }>
                </Book>
    })
    return ret
}


function Category(props : props) {
    return <div className="Category">
        <h3> {props.Name } <FontAwesomeIcon icon={faCaretDown} /></h3>
        <ul>
            {fetch(props)}
        </ul>
        <button onClick={() => fetch(props.Name)} >fetch</button>
    </div>
}

export default Category;
