import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import 'font-awesome/css/font-awesome.min.css';
import { faCaretDown } from '@fortawesome/free-solid-svg-icons';
import Book from './Book'
import { useEffect, useState } from "react";

let books: Array<book> 

interface props {
    Name: string;
}
interface book {
    author: string,
    types:  Array<string>,
    title:  string,
    isbn:   string,
    cover:  string,
}
function fetchBooks(category) {
    let res = fetch("http://localhost:3001/get")
    let Response

    res.then((data) => {Response = data;})

    console.log(Response)

    books = Response as Array<book>;
    console.log(books)
    let ret = []
    books.forEach(book => {
        ret.push( <Book 
                    title =  { book.title  } 
                    author = { book.author } 
                    isbn =   { book.isbn   }
                    cover =  { book.cover  }>
                </Book>)
    })

    console.log(ret)
    return ret    
}


class Category extends React.Component{
    render(props : props) {
    return <div className="Category">
        <h3> {props.Name } <FontAwesomeIcon icon={faCaretDown} /></h3>
        <ul>
            { fetchBooks(props) }
        </ul>
        <button onClick={() => fetchBooks(props.Name)} >fetch</button>
    </div>
    }
}

export default Category;
