import './style/App.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import 'font-awesome/css/font-awesome.min.css';
import { faMagnifyingGlass } from '@fortawesome/free-solid-svg-icons';
import Category from  './Category'
import { useEffect, useState } from "react";


async function fetchCategories() {
  let res = await fetch("http://localhost:3001/catgeories")
  let Response = await res.json();
  return Response[0].result as Array<book>;
}

function App(props) {
return <div className="App">
      <header className="App-header">
        <section>
          <h1>Bücherei</h1>
          <h2>Regale:</h2>
        </section>
        <ul>
          <li>
            <section>
                <h2>Suche<input id="search" placeholder="|"/><FontAwesomeIcon icon={faMagnifyingGlass}/></h2>
                <Category search=""/>
            </section>
          </li>
          <li>
            <Category Name="Krimi" id="crime"/>
          </li>
          <li>
            <Category Name="Sachbuecher" id="`non-fiction`"/>
          </li>
          <li>
            <Category Name="Romane" id="roman"/>
          </li>
          <li>
            <Category Name="Fantasy" id="fantasy"/>
          </li>
          <li>
            <Category Name="für Kinder" id="children"/>
          </li>
        </ul>
      </header>
    </div>
 }

 const [catgeories, initCategories] = useState([])

 useEffect(() => {
     fetchCategories().then((data => {initCategories(data)}))
 },[])

 export default App;
